<?php$this->excel->setActiveSheetIndex(0);//error_reporting(E_ALL);error_reporting(E_ALL & ~E_NOTICE);$this->excel->getDefaultStyle()    ->getAlignment()    ->setHorizontal(PHPExcel_Style_Alignment::HORIZONTAL_LEFT);//name the worksheet$this->excel->getActiveSheet()->setTitle('Thuyết minh tài chính');//save it to Excel5 format (excel 2003 .XLS file), change this to 'Excel2007' (and adjust the filename extension, also the header mime type)//if you want to save it as .XLSX Excel 2007 format$objWriter = PHPExcel_IOFactory::createWriter($this->excel, 'Excel2007');        $md5file = md5(date('YmdHis')) . '.xlsx';//$objWriter = PHPExcel_IOFactory::createWriter($this->excel, 'Excel5');//force user to download the Excel file without writing it to server's HD$filename = 'baocao_congno.xlsx'; //save our workbook as this file name$objWriter->save($filename);if (file_exists($filename)) {    header('Content-Type: application/vnd.ms-excel'); //mime type    header('Content-Disposition: attachment;filename="' . $filename . '"'); //tell browser what's the file name    header('Cache-Control: max-age=0'); //no cache        header('Content-Transfer-Encoding: binary');    header('Expires: 0');    header('Cache-Control: must-revalidate');    header('Pragma: public');    header('Content-Length: ' . filesize($filename));    ob_clean();    flush();    readfile($filename);    exit;}?>